import React, { useState } from 'react';

function ExpenseTracker({ expenses, setExpenses }) {
  const [formData, setFormData] = useState({
    date: new Date().toISOString().split('T')[0],
    category: '',
    amount: '',
    description: ''
  });

  const [editing, setEditing] = useState(null);
  const [filter, setFilter] = useState('all');

  // Categories for expenses
  const categories = [
    'Food',
    'Housing',
    'Transportation',
    'Utilities',
    'Entertainment',
    'Shopping',
    'Healthcare',
    'Education',
    'Personal Care',
    'Travel',
    'Gifts/Donations',
    'Other'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editing !== null) {
      // Update existing expense
      const updatedExpenses = [...expenses];
      updatedExpenses[editing] = formData;
      setExpenses(updatedExpenses);
      setEditing(null);
    } else {
      // Add new expense
      setExpenses([...expenses, formData]);
    }

    // Reset form
    setFormData({
      date: new Date().toISOString().split('T')[0],
      category: '',
      amount: '',
      description: ''
    });
  };

  const handleEdit = (index) => {
    setFormData(expenses[index]);
    setEditing(index);
  };

  const handleDelete = (index) => {
    const updatedExpenses = expenses.filter((_, i) => i !== index);
    setExpenses(updatedExpenses);
  };

  const cancelEdit = () => {
    setEditing(null);
    setFormData({
      date: new Date().toISOString().split('T')[0],
      category: '',
      amount: '',
      description: ''
    });
  };

  // Filter expenses
  const filteredExpenses = filter === 'all' 
    ? expenses
    : expenses.filter(expense => expense.category === filter);

  // Calculate totals
  const totalAmount = filteredExpenses.reduce((sum, expense) => 
    sum + parseFloat(expense.amount || 0), 0);

  // Calculate category totals 
  const categoryTotals = categories.map(category => {
    const total = expenses
      .filter(expense => expense.category === category)
      .reduce((sum, expense) => sum + parseFloat(expense.amount || 0), 0);

    return { category, total };
  }).filter(item => item.total > 0)
    .sort((a, b) => b.total - a.total);

  return (
    <div className="row">
      <div className="col-md-4">
        <div className="card mb-4">
          <div className="card-header">
            {editing !== null ? 'Edit Expense' : 'Add Expense'}
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="date" className="form-label">Date</label>
                <input
                  type="date"
                  className="form-control"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="mb-3">
                <label htmlFor="category" className="form-label">Category</label>
                <select
                  className="form-select"
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select a category</option>
                  {categories.map((category, index) => (
                    <option key={index} value={category}>
                      {category}
                    </option>
                  ))}
                </select>
              </div>

              <div className="mb-3">
                <label htmlFor="amount" className="form-label">Amount ($)</label>
                <input
                  type="number"
                  className="form-control"
                  id="amount"
                  name="amount"
                  step="0.01"
                  min="0"
                  value={formData.amount}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="mb-3">
                <label htmlFor="description" className="form-label">Description</label>
                <textarea
                  className="form-control"
                  id="description"
                  name="description"
                  rows="2"
                  value={formData.description}
                  onChange={handleChange}
                ></textarea>
              </div>

              <div className="d-flex justify-content-between">
                <button type="submit" className="btn btn-primary">
                  {editing !== null ? 'Update Expense' : 'Add Expense'}
                </button>
                {editing !== null && (
                  <button 
                    type="button" 
                    className="btn btn-secondary" 
                    onClick={cancelEdit}
                  >
                    Cancel
                  </button>
                )}
              </div>
            </form>
          </div>
        </div>

        <div className="card">
          <div className="card-header">Expense Summary</div>
          <div className="card-body">
            <h5 className="card-title">Total: ${totalAmount.toFixed(2)}</h5>

            <h6 className="mt-4">Top Categories:</h6>
            <ul className="list-group">
              {categoryTotals.slice(0, 5).map((item, index) => (
                <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  {item.category}
                  <span className="badge bg-primary rounded-pill">
                    ${item.total.toFixed(2)}
                  </span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <div className="col-md-8">
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <span>Expenses</span>
            <div className="dropdown">
              <select 
                className="form-select form-select-sm" 
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                style={{width: '150px'}}
              >
                <option value="all">All Categories</option>
                {categories.map((category, index) => (
                  <option key={index} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="card-body">
            {filteredExpenses.length === 0 ? (
              <p className="text-muted">No expenses recorded yet.</p>
            ) : (
              <div className="table-responsive">
                <table className="table table-hover">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Category</th>
                      <th>Amount</th>
                      <th>Description</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {[...filteredExpenses].reverse().map((expense, index) => (
                      <tr key={index}>
                        <td>{new Date(expense.date).toLocaleDateString()}</td>
                        <td>{expense.category}</td>
                        <td>${parseFloat(expense.amount).toFixed(2)}</td>
                        <td>{expense.description}</td>
                        <td>
                          <button
                            className="btn btn-sm btn-outline-primary me-2"
                            onClick={() => {
                              const realIndex = expenses.findIndex(
                                e => e.date === expense.date && 
                                    e.category === expense.category && 
                                    e.amount === expense.amount && 
                                    e.description === expense.description
                              );
                              handleEdit(realIndex);
                            }}
                          >
                            Edit
                          </button>
                          <button
                            className="btn btn-sm btn-outline-danger"
                            onClick={() => {
                              const realIndex = expenses.findIndex(
                                e => e.date === expense.date && 
                                    e.category === expense.category && 
                                    e.amount === expense.amount && 
                                    e.description === expense.description
                              );
                              handleDelete(realIndex);
                            }}
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ExpenseTracker;
